package com.radproject.hms.models;
import androidx.annotation.NonNull;
import com.google.firebase.firestore.DocumentSnapshot;
import com.google.firebase.firestore.FirebaseFirestore;
import com.google.firebase.firestore.ListenerRegistration;

public class UserModel {
    private String userId;
    private String username;
    private String mobile;
    private String email;
    private String location;
    private double latitude;
    private double longitude;
    private boolean isNICUploaded = false;
    private boolean isUtilityBillUploaded = false;
    private boolean isVerified = false;

    private String Base64Profile = "";

    private ListenerRegistration listenerRegistration;

    public UserModel() {
        // Required empty public constructor
    }

    public UserModel(String userId, String username, String mobile, String email, String location,
                     double latitude, double longitude,
                     boolean isNICUploaded, boolean isUtilityBillUploaded, boolean isVerified) {
        this.userId = userId;
        this.username = username;
        this.mobile = mobile;
        this.email = email;
        this.location = location;
        this.latitude = latitude;
        this.longitude = longitude;
        this.isNICUploaded = isNICUploaded;
        this.isUtilityBillUploaded = isUtilityBillUploaded;
        this.isVerified = isVerified;
    }

    public String getBase64Profile() {
        return Base64Profile;
    }

    public void setBase64Profile(String base64Profile) {
        Base64Profile = base64Profile;
    }

    public String getUserId() {
        return userId;
    }

    public void setUserId(String userId) {
        this.userId = userId;
    }

    public String getUsername() {
        return username;
    }

    public void setUsername(String username) {
        this.username = username;
    }

    public String getMobile() {
        return mobile;
    }

    public void setMobile(String mobile) {
        this.mobile = mobile;
    }

    public String getEmail() {
        return email;
    }

    public void setEmail(String email) {
        this.email = email;
    }

    public String getLocation() {
        return location;
    }

    public void setLocation(String location) {
        this.location = location;
    }

    public boolean isNICUploaded() {
        return isNICUploaded;
    }

    public void setNICUploaded(boolean NICUploaded) {
        isNICUploaded = NICUploaded;
    }

    public boolean isUtilityBillUploaded() {
        return isUtilityBillUploaded;
    }

    public void setUtilityBillUploaded(boolean utilityBillUploaded) {
        isUtilityBillUploaded = utilityBillUploaded;
    }

    public boolean isVerified() {
        return isVerified;
    }

    public void setVerified(boolean verified) {
        isVerified = verified;
    }

    public double getLatitude() {
        return latitude;
    }

    public void setLatitude(double latitude) {
        this.latitude = latitude;
    }

    public double getLongitude() {
        return longitude;
    }

    public void setLongitude(double longitude) {
        this.longitude = longitude;
    }

    public ListenerRegistration addListener(UserModelListener listener) {
        FirebaseFirestore db = FirebaseFirestore.getInstance();
        listenerRegistration = db.collection("Farmer")
                .document(getUserId())
                .addSnapshotListener((documentSnapshot, error) -> {
                    if (error != null) {
                        return;
                    }
                    if (documentSnapshot != null && documentSnapshot.exists()) {
                        DocumentSnapshot document = documentSnapshot;
                        UserModel user = document.toObject(UserModel.class);
                        if (user != null) {
                            setVerified(user.isVerified());
                            listener.onVerificationStatusChanged(user.isVerified());
                        }
                    }
                });
        return listenerRegistration;
    }

    public void removeListener() {
        if (listenerRegistration != null) {
            listenerRegistration.remove();
        }
    }

    public interface UserModelListener {
        void onVerificationStatusChanged(boolean isVerified);
    }

    @Override
    public String toString() {
        return "UserModel{" +
                "userId='" + userId + '\'' +
                ", username='" + username + '\'' +
                ", mobile='" + mobile + '\'' +
                ", email='" + email + '\'' +
                ", location='" + location + '\'' +
                ", latitude=" + latitude +
                ", longitude=" + longitude +
                ", isNICUploaded=" + isNICUploaded +
                ", isUtilityBillUploaded=" + isUtilityBillUploaded +
                ", isVerified=" + isVerified +
                ", listenerRegistration=" + listenerRegistration +
                '}';
    }
}
